import { Box } from "@chakra-ui/layout";
import { Image } from "@chakra-ui/react";
import Home_bg from "../Background/home_bg.jpg";
import { motion } from "framer-motion";
import lemon from "../Icons/lemon_cake.png";
import strawberry from "../Icons/strawberry_cake.png";
import puff from "../Icons/puff.png";
import maffin from "../Icons/maffin.png";

export default function Home() {
  const MotionImage = motion(Image);
  const variants = {
    visible: (i) => ({
      opacity: 1,
      scale: 2.5,
      transition: {
        delay: i * 0.2, // delay 的時間為 i* 0.2
        duration: i,
      },
    }),
    hidden: { opacity: 0 },
  };
  const variants_2 = {
    visible: (i) => ({
      opacity: 1,
      x: i * 5 + "%",
      transition: {
        delay: Math.abs(i) * 1.1, // delay 的時間為 i* 0.2
        duration: 1.5,
      },
    }),
    hidden: { opacity: 0 },
  };
  return (
    <Box
      w="100vw"
      h="100vh"
      bgColor="#ffe067"
      bgImg={Home_bg}
      bgRepeat={"no-repeat"}
      bgSize={"100% auto"}
    >
      <MotionImage
        custom={2}
        animate="visible"
        variants={variants}
        initial={{ opacity: 0 }}
        src={lemon}
        h="20vh"
        top="3vh"
        left="2vw"
        position="absolute"
        // viewport={{ once: false, amount: 0.5 }}
      />
      <MotionImage
        custom={-4}
        animate="visible"
        variants={variants_2}
        initial={{ opacity: 0 }}
        src={strawberry}
        h="30vh"
        top="-2vh"
        right="-35vw"
        position="absolute"
        // viewport={{ once: false, amount: 0.5 }}
      />
      <MotionImage
        custom={2}
        animate="visible"
        variants={variants_2}
        initial={{ opacity: 0 }}
        src={puff}
        h="30vh"
        bottom="0"
        left="-20vw"
        position="absolute"
        // viewport={{ once: false, amount: 0.5 }}
      />
      {/* <MotionImage
        custom={1}
        animate="visible"
        variants={variants}
        src={maffin}
        h="20vh"
        bottom="3vh"
        right="2vw"
        position="absolute"
      /> */}
    </Box>
  );
}
